import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationsService } from "../../../Services/VacationsService";

export function EditVacation(): JSX.Element {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  // State to store the fetched vacation data and form input values
  const [vacation, setVacation] = useState<VacationModel | null>(null);
  const [image, setImage] = useState<File | null>(null);
  const [formData, setFormData] = useState<Omit<VacationModel, "id" | "imageName">>({
    destination: "",
    description: "",
    startDate: "",
    endDate: "",
    price: 0,
    imageUrl: "",
    isLiked: 0,
    likesCount: 0,
  });

  // Fetch vacation details when the component mounts using the vacation ID from the URL
  useEffect(() => {
    const fetchVacation = async () => {
      try {
        // Fetch vacation by ID
        const vacationData = await vacationsService.getVacationById(Number(id));
        setVacation(vacationData);

        // Populate the form with the fetched vacation data
        setFormData({
          destination: vacationData.destination || "",
          description: vacationData.description || "",
          startDate: vacationData.startDate || "",
          endDate: vacationData.endDate || "",
          price: vacationData.price || 0,
          imageUrl: vacationData.imageUrl || "",
          isLiked: vacationData.isLiked || 0,
          likesCount: vacationData.likesCount || 0,
        });

        // Set the image (if any) to the imageName field
        setImage(vacationData.imageName instanceof File ? vacationData.imageName : null);
      } catch (error) {
        console.error("Failed to fetch vacation:", error);
      }
    };

    fetchVacation(); // Call fetch function when component mounts
  }, [id]); // Re-run if vacation ID changes

  // Handle input change for text fields and update form data state
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle file input for image upload and update image state
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    setImage(file || null);
  };

  // Handle form submission to update vacation details
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // Create FormData to handle file and form inputs
      const updatedVacation = new FormData();
      updatedVacation.append("destination", formData.destination);
      updatedVacation.append("description", formData.description);
      updatedVacation.append("startDate", formData.startDate);
      updatedVacation.append("endDate", formData.endDate);
      updatedVacation.append("price", formData.price.toString());
      updatedVacation.append("imageUrl", formData.imageUrl);
      updatedVacation.append("isLiked", formData.isLiked.toString());
      updatedVacation.append("likesCount", formData.likesCount.toString());

      // If a new image is selected, append it to the form data
      if (image) {
        updatedVacation.append("imageName", image); // imageName now expects a File object or null
      }

      // Send the updated vacation data to the backend service
      await vacationsService.editVacation(Number(id), updatedVacation);
      navigate("/vacations"); // Navigate back to vacations list after successful update
    } catch (error) {
      console.error("Failed to update vacation:", error);
    }
  };

  // Show loading screen if vacation data is still being fetched
  if (!vacation) return <div>Loading...</div>;

  return (
    <div className="edit-vacation">
      <h2>Edit Vacation</h2>
      <form onSubmit={handleSubmit}>
        {/* Destination input */}
        <label>
          Destination:
          <input
            type="text"
            name="destination"
            value={formData.destination}
            onChange={handleInputChange}
          />
        </label>

        {/* Description input */}
        <label>
          Description:
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
          />
        </label>

        {/* Start date input */}
        <label>
          Start Date:
          <input
            type="date"
            name="startDate"
            value={formData.startDate}
            onChange={handleInputChange}
          />
        </label>

        {/* End date input */}
        <label>
          End Date:
          <input
            type="date"
            name="endDate"
            value={formData.endDate}
            onChange={handleInputChange}
          />
        </label>

        {/* Price input */}
        <label>
          Price:
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
          />
        </label>

        {/* Image upload input */}
        <label>
          Image:
          <input type="file" accept="image/*" onChange={handleFileChange} />
        </label>

        {/* Hidden fields for imageUrl, isLiked, and likesCount */}
        <input type="hidden" name="imageUrl" value={formData.imageUrl} />
        <input type="hidden" name="isLiked" value={formData.isLiked.toString()} />
        <input type="hidden" name="likesCount" value={formData.likesCount.toString()} />

        <button type="submit">Update Vacation</button>
      </form>
    </div>
  );
}
