import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { vacationsService } from '../../../Services/VacationsService';
import { VacationModel } from '../../../Models/VacationModel';

export function VacationEdit() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [vacation, setVacation] = useState<VacationModel | null>(null);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchVacation = async () => {
            try {
                const vacation = await vacationsService.getVacationById(Number(id));
                setVacation(vacation);
            } catch (error) {
                setError('Failed to fetch vacation');
            }
        };

        fetchVacation();
    }, [id]);

    const handleSave = async () => {
        try {
            if (vacation) {
                await vacationsService.updateVacation(vacation);
                navigate('/vacations'); // Redirect to the vacations list
            }
        } catch (error) {
            setError('Failed to update vacation');
        }
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!vacation) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Edit Vacation</h2>
            <form onSubmit={handleSave}>
                {/* Add form fields here to edit vacation details */}
                <button type="submit">Save</button>
            </form>
        </div>
    );
}
