import { configureStore, createSlice } from "@reduxjs/toolkit";
import { UserModel } from "../Models/UserModel";
import { VacationModel } from "../Models/VacationModel";
import {
  addVacation,
  deleteVacation,
  initUser,
  initVacations,
  logoutUser,
} from "./reducers";

// Application state:
export type AppState = {
  vacations: VacationModel[];
  user: UserModel;
};

// Creating Vacations slice:
const vacationSlice = createSlice({
  name: "vacations", // Internal use
  initialState: null,
  reducers: { initVacations, addVacation, deleteVacation },
});

// Create user slice:
const userSlice = createSlice({
  name: "user",
  initialState: null,
  reducers: { initUser, logoutUser },
});

// Creating action creators:
export const vacationActions = vacationSlice.actions;
export const userActions = userSlice.actions;

// Main redux object:
export const store = configureStore<AppState>({
  reducer: {
    vacations: vacationSlice.reducer, // Vacation state.
    user: userSlice.reducer, // User state
  },
});
