import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { AppState } from "../../../Redux/store";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationActions } from "../../../Redux/store";
import { vacationsService } from "../../../Services/VacationsService";
import { VacationCards } from "../VacationCards/VacationCards";
import { Role } from "../../../Models/enums";
import "./VacationList.css";

export function VacationList(): JSX.Element {
  const [vacations, setVacations] = useState<VacationModel[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const itemsPerPage = 9;
  const dispatch = useDispatch();

  // Retrieve the token and roleId from Redux state
  const token = useSelector((state: AppState) => state.auth.token);
  const roleId = useSelector((state: AppState) => state.user.roleId); // Select roleId from state

  // Determine if the user is an admin based on roleId
  const isAdmin = roleId === Role.Admin;

  // Log isAdmin to verify if the role is being set correctly
  console.log("Is Admin:", isAdmin);

  const userId = useSelector<AppState, number>((state) => state.user.id);

  useEffect(() => {
    if (!token) {
      console.warn("User is not logged in.");
      return;
    }

    vacationsService
      .getAllVacationsByUserId(userId)
      .then((vacations) => {
        if (vacations.length > 0) {
          setVacations(vacations);
          const action = vacationActions.initVacations(vacations);
          dispatch(action);
        } else {
          console.warn("No vacations were fetched from the API.");
        }
      })
      .catch((err) => {
        alert(err.message);
      });
  }, [token, dispatch, userId]);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentVacations = vacations.slice(indexOfFirstItem, indexOfLastItem);

  const nextPage = () => {
    if (currentPage < Math.ceil(vacations.length / itemsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="VacationList">
      <div className="vacation-cards-container">
        {currentVacations.length > 0 ? (
          currentVacations.map((vacation) => (
            <VacationCards
              key={vacation.id}
              vacation={vacation}
              isAdmin={isAdmin} // Pass isAdmin based on roleId
              onToggleLike={() => {}}
            />
          ))
        ) : (
          <p>No vacations available</p>
        )}
      </div>

      <div className="pagination">
        <button onClick={prevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(vacations.length / itemsPerPage)}
        >
          Next
        </button>
      </div>
    </div>
  );
}
