export function VacationList(): JSX.Element {
  const [vacations, setVacations] = useState<VacationModel[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [showLiked, setShowLiked] = useState<boolean>(false); // Toggle state
  const itemsPerPage = 9;
  const dispatch = useDispatch();

  // Retrieve the token and roleId from Redux state
  const token = useSelector((state: AppState) => state.auth.token);
  const roleId = useSelector((state: AppState) => state.user.roleId); // Select roleId from state

  // Convert roleId to a number and check if the user is an admin
  const isAdmin = Number(roleId) === Role.Admin;

  const userId = useSelector<AppState, number>((state) => state.user.id); // Select userId from state

  // Fetch vacations based on the userId
  useEffect(() => {
    if (!token) {
      console.warn("User is not logged in.");
      return;
    }

    vacationsService
      .getAllVacationsByUserId(userId)
      .then((vacations) => {
        if (vacations.length > 0) {
          setVacations(vacations);
          const action = vacationActions.initVacations(vacations);
          dispatch(action);
        } else {
          console.warn("No vacations were fetched from the API.");
        }
      })
      .catch((err) => {
        alert(err.message);
      });
  }, [token, dispatch, userId]);

  // Handle delete action
  const handleDeleteVacation = async (vacationId: number) => {
    try {
      await vacationsService.deleteVacation(vacationId); // Call the service to delete the vacation
      setVacations((prevVacations) =>
        prevVacations.filter((v) => v.id !== vacationId)
      );
    } catch (error) {
      console.error("Failed to delete vacation:", error);
    }
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentVacations = vacations
    .filter((vacation) => !showLiked || vacation.isLiked === 1) // Filter for liked vacations when toggled
    .slice(indexOfFirstItem, indexOfLastItem);

  // Go to the next page
  const nextPage = () => {
    if (currentPage < Math.ceil(vacations.length / itemsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  // Go to the previous page
  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Toggle show liked vacations
  const toggleShowLiked = () => {
    setShowLiked((prevShowLiked) => !prevShowLiked);
    setCurrentPage(1); // Reset pagination to the first page
  };

  return (
    <div className="VacationList">
      <div className="header d-flex">
        {!isAdmin && (
          <div className="form-check form-switch">
            <input
              className="form-check-input"
              type="checkbox"
              role="switch"
              id="flexSwitchCheckChecked"
              checked={showLiked}
              onChange={toggleShowLiked}
            />
            <label
              className="form-check-label"
              htmlFor="flexSwitchCheckChecked"
            >
              {showLiked ? "Show All Vacations" : "Show Liked Vacations"}
            </label>
          </div>
        )}
      </div>

      <div className="vacation-cards-container">
        {currentVacations.length > 0 ? (
          currentVacations.map((vacation) => (
            <VacationCards
              key={vacation.id}
              vacation={vacation}
              isAdmin={isAdmin} // Pass isAdmin based on roleId
              onToggleLike={() => {
                console.log("Like toggle function called");
              }}
              onDeleteVacation={handleDeleteVacation} // Pass delete handler
            />
          ))
        ) : (
          <p>No vacations available</p>
        )}
      </div>

      {/* Pagination controls */}
      <div className="pagination">
        <button onClick={prevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(vacations.length / itemsPerPage)}
        >
          Next
        </button>
      </div>
    </div>
  );
}
