import axios from "axios";
// import { jwtDecode } from "jwt-decode";
import { CredentialsModel } from "../Models/CredentialsModel";
import { UserModel } from "../Models/UserModel";
import { store, userActions } from "../Redux/store";
import { appConfig } from "../Utils/AppConfig";

class UserService {
  public constructor() {
    const token = localStorage.getItem("token");
    console.log("Loaded token in UserService constructor:", token); // לוג לבדיקה

    if (!token) return;

    // Set token in Axios header
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    console.log("Token set in Axios in UserService constructor:", axios.defaults.headers.common["Authorization"]);

    try {
      // Decode the token to get user details
      const container = jwtDecode<{ user: UserModel }>(token);
      const dbUser = container.user;
      console.log("Decoded user in UserService constructor:", dbUser);

      // Dispatch the user to redux store
      const action = userActions.initUser(dbUser);
      store.dispatch(action);
    } catch (error) {
      console.error("Error decoding token in UserService constructor:", error);
    }
  }

  public async register(user: UserModel) {
    const response = await axios.post<{ token: string; user: UserModel }>(
      appConfig.registerUrl,
      user
    );
    const { token, user: dbUser } = response.data;

    localStorage.setItem("token", token);

    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    console.log("Token set in Axios after register:", axios.defaults.headers.common["Authorization"]);

    const action = userActions.initUser(dbUser);
    store.dispatch(action);
  }

  public async login(credentials: CredentialsModel): Promise<UserModel> {
    const response = await axios.post<{ token: string; user: UserModel }>(
      appConfig.loginUrl,
      credentials
    );
    const { token, user: dbUser } = response.data;

    localStorage.setItem("token", token);

    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    console.log("Token set in Axios after login:", axios.defaults.headers.common["Authorization"]);

    const action = userActions.initUser(dbUser);
    store.dispatch(action);

    return dbUser;
  }

  public logout() {
    localStorage.removeItem("token");

    delete axios.defaults.headers.common["Authorization"];
    console.log("Token removed from Axios after logout");

    const action = userActions.logoutUser();
    store.dispatch(action);
  }
}

export const userService = new UserService();
