import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationsService } from "../../../Services/VacationsService";
import "./VacationEdit.css";

export function EditVacation(): JSX.Element {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const [vacation, setVacation] = useState<VacationModel | null>(null);
  const [image, setImage] = useState<File | null>(null);
  const [formData, setFormData] = useState<
    Omit<VacationModel, "id" | "imageName">
  >({
    destination: "",
    description: "",
    startDate: "",
    endDate: "",
    price: 0,
    imageUrl: "",
    isLiked: 0,
    likesCount: 0,
  });
  const [errors, setErrors] = useState<string[]>([]);

  // Fetch vacation details and populate the form when the component mounts
  useEffect(() => {
    const fetchVacation = async () => {
      try {
        const vacationData = await vacationsService.getVacationById(Number(id));
        setVacation(vacationData);

        // Populate form with existing vacation data
        setFormData({
          destination: vacationData.destination || "",
          description: vacationData.description || "",
          startDate: vacationData.startDate || "",
          endDate: vacationData.endDate || "",
          price: vacationData.price || 0,
          imageUrl: vacationData.imageUrl || "",
          isLiked: vacationData.isLiked || 0,
          likesCount: vacationData.likesCount || 0,
        });
      } catch (error) {
        console.error("Failed to fetch vacation:", error);
      }
    };

    fetchVacation();
  }, [id]);

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    setImage(file || null);
  };

  // Validation for form submission
  const validateForm = () => {
    const newErrors: string[] = [];

    // Check required fields (except image)
    if (!formData.destination) {
      newErrors.push("Destination is required.");
    }
    if (!formData.description) {
      newErrors.push("Description is required.");
    }
    if (!formData.startDate) {
      newErrors.push("Start date is required.");
    }
    if (!formData.endDate) {
      newErrors.push("End date is required.");
    }

    // Check price (should be positive and less than 10,000)
    if (formData.price < 0 || formData.price > 10000) {
      newErrors.push("Price must be between 0 and 10,000.");
    }

    // Check that end date is not before start date
    if (new Date(formData.endDate) < new Date(formData.startDate)) {
      newErrors.push("End date cannot be before the start date.");
    }

    return newErrors;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Run validation before submitting the form
    const formErrors = validateForm();
    if (formErrors.length > 0) {
      setErrors(formErrors);
      return;
    }

    try {
      const updatedVacation = new FormData();
      updatedVacation.append("destination", formData.destination);
      updatedVacation.append("description", formData.description);
      updatedVacation.append("startDate", formData.startDate);
      updatedVacation.append("endDate", formData.endDate);
      updatedVacation.append("price", formData.price.toString());
      updatedVacation.append("imageUrl", formData.imageUrl);
      updatedVacation.append("isLiked", formData.isLiked.toString());
      updatedVacation.append("likesCount", formData.likesCount.toString());

      // Append the image only if it's selected
      if (image) {
        updatedVacation.append("imageName", image);
      }

      // Update vacation in the service
      await vacationsService.updateVacation(Number(id), updatedVacation);
      navigate("/vacations");
    } catch (error) {
      console.error("Failed to update vacation:", error);
    }
  };

  if (!vacation) return <div>Loading...</div>;

  return (
    <div className="EditVacation">
      <h2>Edit Vacation</h2>

      {errors.length > 0 && (
        <ul className="error-list">
          {errors.map((error, index) => (
            <li key={index} className="error-item">
              {error}
            </li>
          ))}
        </ul>
      )}

      <form className="edit-form" onSubmit={handleSubmit}>
        <div className="input-wrapper">
          <label htmlFor="destination">Destination:</label>
          <input
            id="destination"
            type="text"
            name="destination"
            value={formData.destination}
            onChange={handleInputChange}
          />
        </div>

        <div className="input-wrapper">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
          />
        </div>

        <div className="input-wrapper">
          <label htmlFor="startDate">Start Date:</label>
          <input
            id="startDate"
            type="date"
            name="startDate"
            value={formData.startDate}
            onChange={handleInputChange}
          />
        </div>

        <div className="input-wrapper">
          <label htmlFor="endDate">End Date:</label>
          <input
            id="endDate"
            type="date"
            name="endDate"
            value={formData.endDate}
            onChange={handleInputChange}
          />
        </div>

        <div className="input-wrapper">
          <label htmlFor="price">Price:</label>
          <input
            id="price"
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
          />
        </div>

        <div className="input-wrapper">
          <label htmlFor="image">Image (optional):</label>
          <input
            id="image"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
          />
        </div>

        <button className="submit-btn" type="submit">
          Update Vacation
        </button>
      </form>
    </div>
  );
}
