import { useForm } from "react-hook-form";
import { NavLink, useNavigate } from "react-router-dom";
import { UserModel } from "../../../Models/UserModel";
import { errorHandler } from "../../../Services/errorHandler";
import { userService } from "../../../Services/UserService";
import { notify } from "../../../Utils/Notify";
import "./Register.css";

export function Register(): JSX.Element {

    const { register, handleSubmit } = useForm<UserModel>();
    const navigate = useNavigate(); // Initialize navigate hook

    async function send(user: UserModel) {
        try {
            // Register the user and automatically log them in
            await userService.register(user);
            notify.success("Welcome " + user.firstName);

            // Redirect the user after successful registration
            navigate("/login"); // Perform navigation after registration success
        }
        catch (err: any) {
            notify.error(errorHandler.getError(err)); // Handle errors
        }
    }

    return (
        <div className="register-container">
            <form className="register-form" onSubmit={handleSubmit(send)}>
                <h2>Register</h2>
                <div className="form-group">

                    <label htmlFor="firstName">First Name</label>
                    <input type="text" id="firstName" {...register("firstName")} />

                </div>
                <div className="form-group">

                    <label htmlFor="lastName">Last Name</label>
                    <input type="text" id="lastName" {...register("lastName")} />

                </div>
                <div className="form-group">

                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" {...register("email")} />

                </div>
                <div className="form-group">

                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" {...register("password")} />

                </div>

                <button type="submit" className="register-button">Register</button>

                <div className="member-area">
                    <p>Already a member?</p>
                    <NavLink to="/login">Login</NavLink>
                </div>
            </form>
        </div>
    );
}
