import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { VacationModel } from "../../../Models/VacationModel";
import { errorHandler } from "../../../Services/errorHandler";
import { vacationsService } from "../../../Services/VacationsService";
import { notify } from "../../../Utils/Notify";
import "./AddVacation.css";

export function AddVacation(): JSX.Element {
  const { register, handleSubmit, watch } = useForm<VacationModel>();
  const navigate = useNavigate();
  const watchStartDate = watch("startDate");
  const watchEndDate = watch("endDate");

  async function send(vacation: VacationModel) {
    try {
      // Date validation
      if (
        watchStartDate &&
        watchEndDate &&
        new Date(watchEndDate) < new Date(watchStartDate)
      ) {
        notify.error("End Date cannot be before Start Date.");
        return;
      }

      // Ensure imageName is not null or undefined
      if (!vacation.imageName) {
        notify.error("Image is required.");
        return;
      }

      await vacationsService.addVacation(vacation);
      notify.success("Vacation has been added.");
      navigate("/vacations");
    } catch (err: any) {
      notify.error(errorHandler.getError(err));
    }
  }

  return (
    <div className="addVacation">
      <form onSubmit={handleSubmit(send)}>
        <label>Destination: </label>
        <input type="text" {...register("destination")} required />

        <label>Description: </label>
        <textarea rows={10} cols={38} {...register("description")} required />

        <label>Start Date: </label>
        <input type="date" {...register("startDate")} required />

        <label>End Date: </label>
        <input type="date" {...register("endDate")} required />

        <label>Price: </label>
        <input
          type="number"
          {...register("price")}
          required
          placeholder="$$$"
          min="0"
          max="10000"
        />
        <label>Image: </label>
        <input
          type="file"
          {...register("imageName")}
          accept="image/*"
          required
        />

        <button type="submit">Add Vacation</button>
      </form>
    </div>
  );
}

