// UserService.ts
import axios from "axios";
import jwtDecode from "jwt-decode";
import { CredentialsModel } from "../Models/CredentialsModel";
import { UserModel } from "../Models/UserModel";
import { store, userActions } from "../Redux/store";
import { appConfig } from "../Utils/AppConfig";

class UserService {
  public constructor() {
    const token = localStorage.getItem("token");
    if (!token) return;

    // Set token in Axios header
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    try {
      // Decode the token to get user details
      const container = jwtDecode<{ user: UserModel }>(token);
      const dbUser = container.user;

      // Dispatch the user to redux store
      const action = userActions.initUser(dbUser);
      store.dispatch(action);
    } catch (error) {
      console.error("Invalid token format");
    }
  }

  public async register(user: UserModel) {
    // Send user to backend:
    const response = await axios.post<{ token: string; user: UserModel }>(
      appConfig.registerUrl,
      user
    );

    // Get token and user:
    const { token, user: dbUser } = response.data;

    // Save token to storage:
    localStorage.setItem("token", token);

    // Set token in Axios header
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    // Send to redux:
    const action = userActions.initUser(dbUser);
    store.dispatch(action);
  }

  public async login(credentials: CredentialsModel): Promise<UserModel> {
    // Send credentials to backend:
    const response = await axios.post<{ token: string; user: UserModel }>(
      appConfig.loginUrl,
      credentials
    );

    // Get token and user from response:
    const { token, user: dbUser } = response.data;

    // Save token to localStorage:
    localStorage.setItem("token", token);

    // Set token in Axios default headers for future requests:
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    // Dispatch user to redux:
    const action = userActions.initUser(dbUser);
    store.dispatch(action);

    // Return the logged-in user object
    return dbUser;
  }

  public logout() {
    // Remove token from storage
    localStorage.removeItem("token");

    // Remove token from Axios header
    delete axios.defaults.headers.common["Authorization"];

    const action = userActions.logoutUser();
    store.dispatch(action);
  }
}

export const userService = new UserService();
