import React, { useState, useEffect } from "react";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationsService } from "../../../Services/VacationsService";
import { Link } from "react-router-dom"; // Assuming you're using react-router for navigation
import "./VacationCards.css";

interface VacationCardProps {
  vacation: VacationModel;
  onToggleLike: () => void;
  isAdmin: boolean; // Add isAdmin prop to indicate if the user is an admin
}

export function VacationCards(props: VacationCardProps): JSX.Element {
  const { vacation, onToggleLike, isAdmin } = props;

  // Initialize likes count and like state from the vacation props
  const initialLikesCount = Number(vacation.likesCount) || 0;
  const initialIsLiked = vacation.isLiked === 1;

  // State for managing the likes count and whether the vacation is liked by the user
  const [likesCount, setLikesCount] = useState<number>(initialLikesCount);
  const [isLiked, setIsLiked] = useState<boolean>(initialIsLiked);

  // Function to handle like button click
  const handleToggleLike = async () => {
    try {
      // Toggle like status by sending request to backend and get updated vacation data
      const updatedVacation = await vacationsService.toggleLike(vacation.id);

      // Update likes count and like status based on the response from the backend
      setLikesCount(updatedVacation.likesCount);  
      setIsLiked(updatedVacation.isLiked === 1);  

      // Call external function to notify the parent component to update the vacation list
      onToggleLike();  
    } catch (error) {
      console.error("Failed to toggle like:", error);
    }
  };

  // Function to format date to display in Israeli format
  const formatDate = (date: string): string => {
    return new Date(date).toLocaleDateString("he-IL");
  };

  // useEffect to update state when vacation details change
  useEffect(() => {
    // Whenever the vacation props update, reset the like count and like state
    setLikesCount(Number(vacation.likesCount) || 0);
    setIsLiked(vacation.isLiked === 1);
  }, [vacation.likesCount, vacation.isLiked]);

  return (
    <div className="VacationCards">
      <div className="card">
        {/* Display vacation image */}
        <img className="card-img-top" src={vacation.imageUrl} alt="vacation" />
        <div className="card-body">
          <h5 className="card-title">{vacation.destination}</h5>
          <span className="date-range">
            {/* Display formatted start and end dates */}
            {formatDate(vacation.startDate)} - {formatDate(vacation.endDate)}
          </span>
          <p className="card-text">{vacation.description}</p>
          <div className="price-box">${vacation.price}</div>

          {/* Display Like/Unlike buttons only if the user is NOT an Admin */}
          {!isAdmin && (
            <div className="like-container">
              {/* Button to toggle like status */}
              <button
                className={`like-button ${isLiked ? "liked" : ""}`}
                onClick={handleToggleLike}
              >
                {/* Heart icon and like count */}
                <span>❤️</span> {likesCount}
              </button>
            </div>
          )}

          {/* Display Edit button if the user is an Admin */}
          {isAdmin && (
            <Link
              to={`/edit-vacation/${vacation.id}`}
              className="btn btn-primary"
            >
              Edit Vacation
            </Link>
          )}
        </div>
      </div>
    </div>
  );
}
