import axios, { AxiosRequestConfig } from "axios";
import { VacationModel } from "../Models/VacationModel";
import { store, vacationActions } from "../Redux/store";
import { appConfig } from "../Utils/AppConfig";

class VacationsService {
  public async getAllVacationsByUserId(userId: number): Promise<VacationModel[]> {
    const token = store.getState().auth?.token;
    if (!token) {
      throw new Error("Unauthorized access. Please log in.");
    }

    try {
      const response = await axios.get<VacationModel[]>(
        `${appConfig.vacationsUrl}/user/${userId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      return response.data;
    } catch (error) {
      console.error("Failed to fetch vacations by user ID:", error);
      throw error;
    }
  }

  // Fetch vacation by ID
  public async getVacationById(vacationId: number): Promise<VacationModel> {
    const token = store.getState().auth?.token;
    if (!token) {
      throw new Error("Unauthorized access. Please log in.");
    }

    try {
      const response = await axios.get<VacationModel>(
        `${appConfig.vacationsUrl}/${vacationId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      return response.data;
    } catch (error) {
      console.error("Failed to fetch vacation by ID:", error);
      throw error;
    }
  }

  // Add a new vacation
  public async addVacation(vacation: VacationModel): Promise<VacationModel> {
    const token = store.getState().auth?.token;
    if (!token) {
      throw new Error("Unauthorized access. Please log in.");
    }

    const formData = new FormData();
    formData.append("destination", vacation.destination);
    formData.append("description", vacation.description);
    formData.append("startDate", vacation.startDate);
    formData.append("endDate", vacation.endDate);
    formData.append("price", vacation.price.toString());
    if (vacation.imageName) {
      formData.append("imageName", vacation.imageName);
    }

    try {
      const response = await axios.post<VacationModel>(
        appConfig.vacationsUrl,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const addedVacation = response.data;

      // Dispatch the added vacation to the Redux store
      const action = vacationActions.addVacation(addedVacation);
      store.dispatch(action);

      return addedVacation;
    } catch (error) {
      console.error("Failed to add vacation:", error);
      throw error;
    }
  }

  // Update an existing vacation
  public async updateVacation(
    vacationId: number,
    formData: FormData
  ): Promise<VacationModel> {
    const token = store.getState().auth?.token;
    if (!token) {
      throw new Error("Unauthorized access. Please log in.");
    }

    try {
      const response = await axios.put<VacationModel>(
        `${appConfig.vacationsUrl}/${vacationId}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const updatedVacation = response.data;

      // Dispatch the updated vacation to the Redux store
      store.dispatch(vacationActions.updateVacation(updatedVacation));
      return updatedVacation;
    } catch (error) {
      console.error("Failed to update vacation:", error);
      throw error;
    }
  }

  // Delete a vacation
  public async deleteVacation(vacationId: number): Promise<void> {
    const token = store.getState().auth?.token;
    if (!token) {
      throw new Error("Unauthorized access. Please log in.");
    }

    try {
      await axios.delete(`${appConfig.vacationsUrl}/${vacationId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      store.dispatch(vacationActions.deleteVacation(vacationId));
    } catch (error) {
      console.error("Failed to delete vacation:", error);
      throw error;
    }
  }

  // Toggle like on vacation
  public async toggleLike(vacationId: number): Promise<VacationModel> {
    const token = store.getState().auth?.token;
    if (!token) {
      throw new Error("Unauthorized access. Please log in.");
    }

    try {
      const response = await axios.patch<VacationModel>(
        `${appConfig.vacationsUrl}/toggle-like/${vacationId}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      const updatedVacation = response.data;

      // Dispatch the updated vacation to the Redux store
      store.dispatch(vacationActions.updateVacation(updatedVacation));
      return updatedVacation;
    } catch (error) {
      console.error("Failed to toggle like:", error);
      throw error;
    }
  }
}

export const vacationsService = new VacationsService();
