import axios, { AxiosRequestConfig } from "axios";
import { VacationModel } from "../Models/VacationModel";
import { store, vacationActions } from "../Redux/store";
import { appConfig } from "../Utils/AppConfig";

class VacationsService {
  // Function to get all vacations
  public async getAllVacations(): Promise<VacationModel[]> {
    try {
      axios 
    }catch {

    }
  }

  // Function to add a vacation
  public async addVacation(vacation: VacationModel): Promise<void> {
    try {
      const token = store.getState().auth?.token;
      if (!token) {
        console.warn("Token is not available.");
        throw new Error("Unauthorized access. Please log in.");
      }

      const options: AxiosRequestConfig = {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
      };

      const response = await axios.post<VacationModel>(
        appConfig.vacationsUrl,
        vacation,
        options
      );

      const addedVacation = response.data;

      // Dispatch the added vacation to the Redux store
      const action = vacationActions.addVacation(addedVacation);
      store.dispatch(action);

      // Fetch all vacations again to ensure the list is up-to-date
      await this.getAllVacations();
    } catch (error) {
      console.error("Failed to add vacation:", error);
      throw error;
    }
  }

  // Function to delete a vacation by id
  public async deleteVacation(vacationId: number): Promise<void> {
    try {
      const token = store.getState().auth?.token;
      if (!token) {
        console.warn("Token is not available.");
        throw new Error("Unauthorized access. Please log in.");
      }

      await axios.delete<VacationModel>(
        `${appConfig.vacationsUrl}${vacationId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const action = vacationActions.deleteVacation(vacationId);
      store.dispatch(action);
    } catch (error) {
      console.error("Failed to delete vacation:", error);
      throw error;
    }
  }

  // Function to toggle like a vacation by id
  public async toggleLike(vacationId: number): Promise<VacationModel> {
    try {
      const token = store.getState().auth?.token;
      if (!token) {
        console.warn("Token is not available.");
        throw new Error("Unauthorized access. Please log in.");
      }

      const response = await axios.patch<VacationModel>(
        `${appConfig.vacationsUrl}toggle-like/${vacationId}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const updatedVacation = response.data;

      // Update the Redux store with the updated vacation
      const action = vacationActions.updateVacation(updatedVacation);
      store.dispatch(action);

      return updatedVacation;
    } catch (error) {
      console.error("Failed to toggle like:", error);
      throw error;
    }
  }

  // Function to sort vacations in the Redux store
  private sortVacations(): void {
    const state = store.getState();
    if (!state.vacations || state.vacations.length === 0) return;

    const sortedVacations = [...state.vacations].sort(
      (a, b) =>
        new Date(a.startDate).getTime() - new Date(b.startDate).getTime()
    );

    const action = vacationActions.initVacations(sortedVacations);
    store.dispatch(action);
  }
}

export const vacationsService = new VacationsService();
