import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { AppState } from "../../../Redux/store";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationActions } from "../../../Redux/store";
import { vacationsService } from "../../../Services/VacationsService";
import { VacationCards } from "../VacationCards/VacationCards";
import "./VacationList.css";

export function VacationList(): JSX.Element {
  const [vacations, setVacations] = useState<VacationModel[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [itemsPerPage] = useState<number>(9);
  const dispatch = useDispatch();

  // Retrieve the token from Redux state
  const token = useSelector((state: AppState) => state.auth.token);

  useEffect(() => {
    if (token) {
      console.log("Fetching vacations with token:", token); // Debugging log
      // If the token exists, proceed with the API call
      vacationsService
        .getAllVacations()
        .then((vacations) => {
          console.log("Fetched vacations:", vacations); // Debugging log
          setVacations(vacations); // Update local state with fetched vacations
          const action = vacationActions.initVacations(vacations); // Create an action to initialize vacations in Redux
          dispatch(action); // Dispatch the action to update the Redux store
        })
        .catch((err) => console.error("Error fetching vacations:", err)); // Handle errors in fetching vacations
    }
  }, [token, dispatch]); // Dependency array ensures the effect runs when token or dispatch changes

  // Get vacations from Redux state
  const vacationsFromRedux = useSelector(
    (state: AppState) => state.vacations.vacations
  );

  // Calculate total number of pages based on vacations count
  const totalPages = Math.ceil(vacations.length / itemsPerPage);

  // Determine which vacations to show on the current page
  const currentVacations = vacationsFromRedux.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <div className="VacationList">
      {vacations.length === 0 ? (
        <p>No vacations available</p> // Show message if no vacations are available
      ) : (
        currentVacations.map((v) => <VacationCards key={v.id} vacation={v} onToggleLike={function (): void {
            throw new Error("Function not implemented.");
        } } />) // Render vacation cards
      )}

      {/* Pagination controls */}
      {vacations.length > itemsPerPage && (
        <div className="Pagination">
          {[...Array(totalPages).keys()].map((page) => (
            <button
              key={page}
              onClick={() => setCurrentPage(page + 1)}
              className={currentPage === page + 1 ? "Active" : ""}
            >
              {page + 1}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}
