// VacationEdit.tsx
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationsService } from "../../../Services/VacationsService";
import { notify } from "../../../Utils/Notify";
import { AppState } from "../../../Redux/store";

export function VacationEdit(): JSX.Element {
  const { register, handleSubmit, setValue } = useForm<VacationModel>();
  const navigate = useNavigate();
  const vacationId = 1; // תקבע לפי הדינמיקה של היישום שלך

  useEffect(() => {
    async function fetchVacation() {
      try {
        const vacation = await vacationsService.getVacationById(vacationId);
        setValue("destination", vacation.destination);
        setValue("description", vacation.description);
        setValue("startDate", vacation.startDate);
        setValue("endDate", vacation.endDate);
        setValue("price", vacation.price);
      } catch (err) {
        notify.error("Failed to fetch vacation");
      }
    }
    fetchVacation();
  }, [vacationId, setValue]);

  async function send(vacation: VacationModel) {
    try {
      const formData = new FormData();
      formData.append("destination", vacation.destination);
      formData.append("description", vacation.description);
      formData.append("startDate", vacation.startDate);
      formData.append("endDate", vacation.endDate);
      formData.append("price", vacation.price.toString());

      // Only if there's a new image file selected
      if (vacation.imageName) {
        formData.append("imageName", vacation.imageName as Blob);
      }

      await vacationsService.updateVacation(vacationId, formData);

      notify.success("Vacation updated successfully");
      navigate("/vacations");
    } catch (err) {
      notify.error("Failed to update vacation");
    }
  }

  return (
    <div className="vacation-edit">
      <form onSubmit={handleSubmit(send)}>
        <input {...register("destination")} placeholder="Destination" />
        <textarea {...register("description")} placeholder="Description" />
        <input type="date" {...register("startDate")} />
        <input type="date" {...register("endDate")} />
        <input type="number" {...register("price")} placeholder="Price" />
        <input type="file" {...register("imageName")} />
        <button type="submit">Update Vacation</button>
      </form>
    </div>
  );
}
