import { useForm } from "react-hook-form";
import { NavLink, useNavigate } from "react-router-dom";
import { CredentialsModel } from "../../../Models/CredentialsModel";
import { errorHandler } from "../../../Services/errorHandler";
import { userService } from "../../../Services/UserService";
import { notify } from "../../../Utils/Notify";
import { useDispatch } from "react-redux"; // Import useDispatch from react-redux
import { authActions } from "../../../Redux/store"
import "./Login.css";

export function Login(): JSX.Element {
  const { register, handleSubmit } = useForm<CredentialsModel>();
  const navigate = useNavigate();
  const dispatch = useDispatch(); // Create a dispatch function to use Redux actions

  async function send(credentials: CredentialsModel) {
    try {
      const token = await userService.login(credentials); // Assuming login returns the token
      dispatch(authActions.login(token)); // Dispatch the login action to save the token in Redux
      notify.success("Welcome back!");
      navigate("/vacations"); // Navigate to the vacations page
    } catch (err: any) {
      const errorMessage = errorHandler.getError(err);
      notify.error(errorMessage);
    }
  }

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit(send)}>
        <h2>Login</h2>
        <div>
          <label>Email</label>
          <input type="email" id="email" {...register("email")} />
        </div>
        <div>
          <label>Password</label>
          <input type="password" id="password" {...register("password")} />
        </div>
        <button>Login</button>
        <div className="register-area">
          <span>Don't have an account? </span>
          <NavLink to="/register">Register</NavLink>
        </div>
      </form>
    </div>
  );
}
