import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { useParams, useNavigate } from "react-router-dom";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationsService } from "../../../Services/VacationsService";
import "./VacationEdit.css";

export function EditVacation(): JSX.Element {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  // Using react-hook-form for form validation
  const {
    register,
    handleSubmit,
    setValue,
    getValues,
    formState: { errors },
  } = useForm<VacationModel>();

  const [image, setImage] = useState<File | null>(null);

  useEffect(() => {
    const fetchVacation = async () => {
      try {
        const vacationData = await vacationsService.getVacationById(Number(id));

        // Populate form with existing vacation data
        setValue("destination", vacationData.destination || "");
        setValue("description", vacationData.description || "");
        setValue("startDate", vacationData.startDate || "");
        setValue("endDate", vacationData.endDate || "");
        setValue("price", vacationData.price || 0);
      } catch (error) {
        console.error("Failed to fetch vacation:", error);
      }
    };

    fetchVacation();
  }, [id, setValue]);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    setImage(file || null);
  };

  const onSubmit = async (formData: VacationModel) => {
    try {
      const updatedVacation = new FormData();
      updatedVacation.append("destination", formData.destination);
      updatedVacation.append("description", formData.description);
      updatedVacation.append("startDate", formData.startDate);
      updatedVacation.append("endDate", formData.endDate);
      updatedVacation.append("price", formData.price.toString());

      if (image) {
        updatedVacation.append("imageName", image);
      }

      await vacationsService.editVacation(Number(id), updatedVacation); // Call with FormData
      navigate("/vacations");
    } catch (error) {
      console.error("Failed to update vacation:", error);
    }
};


  return (
    <div className="EditVacation">
      <h2>Edit Vacation</h2>

      <form className="edit-form" onSubmit={handleSubmit(onSubmit)}>
        <div className="input-wrapper">
          <label htmlFor="destination">Destination:</label>
          <input
            id="destination"
            type="text"
            {...register("destination", {
              required: "Destination is required",
              minLength: {
                value: 3,
                message: "Destination must be at least 3 characters long",
              },
            })}
          />
          {errors.destination && (
            <span className="error">{errors.destination.message}</span>
          )}
        </div>

        <div className="input-wrapper">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            {...register("description", {
              required: "Description is required",
              maxLength: {
                value: 500,
                message: "Description cannot exceed 500 characters",
              },
            })}
          />
          {errors.description && (
            <span className="error">{errors.description.message}</span>
          )}
        </div>

        <div className="input-wrapper">
          <label htmlFor="startDate">Start Date:</label>
          <input
            id="startDate"
            type="date"
            {...register("startDate", {
              required: "Start date is required",
            })}
          />
          {errors.startDate && (
            <span className="error">{errors.startDate.message}</span>
          )}
        </div>

        <div className="input-wrapper">
          <label htmlFor="endDate">End Date:</label>
          <input
            id="endDate"
            type="date"
            {...register("endDate", {
              required: "End date is required",
              validate: (value) =>
                new Date(value) >= new Date(getValues("startDate")) ||
                "End date cannot be before start date",
            })}
          />
          {errors.endDate && (
            <span className="error">{errors.endDate.message}</span>
          )}
        </div>

        <div className="input-wrapper">
          <label htmlFor="price">Price:</label>
          <input
            id="price"
            type="number"
            {...register("price", {
              required: "Price is required",
              min: { value: 1, message: "Price must be positive" },
              max: { value: 10000, message: "Price cannot exceed 10,000" },
            })}
          />
          {errors.price && (
            <span className="error">{errors.price.message}</span>
          )}
        </div>

        <div className="input-wrapper">
          <label htmlFor="image">Image (optional):</label>
          <input
            id="image"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
          />
        </div>

        <button className="submit-btn" type="submit">
          Update Vacation
        </button>
      </form>
    </div>
  );
}
