import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationActions } from "../../../Redux/store";
import { vacationsService } from "../../../Services/VacationsService";
import { VacationCards } from "../VacationCards/VacationCards";
import "./VacationList.css";
import axios from "axios";

export function VacationList(): JSX.Element {
  const [vacations, setVacations] = useState<VacationModel[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [itemsPerPage] = useState<number>(9);
  const dispatch = useDispatch();

  // Set token in the header for all axios requests
  useEffect(() => {
    const token = localStorage.getItem("token"); // Get the token from local storage
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`; // Set token in the headers
    }

    // Fetch vacations only if the token exists
    if (token) {
      vacationsService
        .getAllVacations()
        .then((vacations) => {
          setVacations(vacations);
          const action = vacationActions.initVacations(vacations);
          dispatch(action);
        })
        .catch((err) => alert(err.message));
    } else {
      alert("You are not logged in."); // Notify the user that they are not logged in
    }
  }, [dispatch]);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentVacations = vacations.slice(indexOfFirstItem, indexOfLastItem);

  const nextPage = () => {
    if (currentPage < Math.ceil(vacations.length / itemsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="VacationList">
      <div className="vacation-cards-container">
        {currentVacations.map((vacation) => (
          <VacationCards
            key={vacation.id}
            vacation={vacation}
            onToggleLike={() => {}}
          />
        ))}
      </div>

      <div className="pagination">
        <button onClick={prevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={nextPage}
          disabled={currentPage === Math.ceil(vacations.length / itemsPerPage)}
        >
          Next
        </button>
      </div>
    </div>
  );
}
