import { useEffect } from "react";
import { useDispatch } from "react-redux";
import axios from "axios";
import jwtDecode from "jwt-decode";
import { userActions } from "../../../Redux"; // Redux action
import { UserModel } from "../Models/UserModel"; // User Model
import Copyrights from "../Components/Copyrights/Copyrights";
import Header from "../Components/Header/Header";
import Menu from "../Components/Menu/Menu";
import Routing from "../Components/Routing/Routing"; // Use Routing component for navigation
import "./Layout.css";

function App(): JSX.Element {
  const dispatch = useDispatch();

  // Load token from localStorage and set it in Axios headers
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      // Set token in Axios header
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

      try {
        // Decode token to get user information
        const user = jwtDecode<{ user: UserModel }>(token).user;

        // Dispatch user info to Redux
        dispatch(userActions.initUser(user));
      } catch (error) {
        console.error("Invalid token format");
      }
    }
  }, [dispatch]);

  return (
    <div className="Layout">
      <header>
        <Header />
      </header>
      <nav>
        <Menu />
      </nav>
      <main>
        <Routing />
      </main>
      <footer>
        <Copyrights />
      </footer>
    </div>
  );
}

export default App;
