import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import { vacationsService } from "../../../Services/VacationsService";
import { Chart, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";
import { saveAs } from "file-saver"; // file-saver for CSV download
import { useNavigate } from "react-router-dom";
import "./VacationReport.css";

Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export function VacationReport(): JSX.Element {
    const [reportData, setReportData] = useState<{ destination: string; likesCount: number; }[]>([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchReportData = async () => {
            try {
                const data = await vacationsService.getVacationLikesReport();
                setReportData(data);
            } catch (err) {
                console.error("Failed to fetch vacation report:", err);
            }
        };
        fetchReportData();
    }, []);

    const chartData = {
        labels: reportData.map((vacation) => vacation.destination),
        datasets: [
            {
                label: "Number of Likes",
                data: reportData.map((vacation) => vacation.likesCount),
                backgroundColor: "rgb(13, 110, 253)",
            },
        ],
    };

    const chartOptions = {
        responsive: true,
        plugins: {
            legend: {
                display: true,
                position: "top" as const,
            },
            title: {
                display: true,
                text: "Vacation Likes Report",
            },
        },
    };

    const handleDownloadCSV = () => {
        // Step 1: Create the CSV content as a string
        // The content starts with "Destination,Likes" as the headers,
        // followed by rows where each row contains a destination and its likes.
        const csvContent =
            "data:text/csv;charset=utf-8," + // Specify the MIME type as CSV with UTF-8 encoding.
            "Destination,Likes\n" + // Add column headers (Destination, Likes).
            reportData // Map through the report data.
                .map((vacation) => `${vacation.destination},${vacation.likesCount}`) // Format each entry as 'destination,likesCount'.
                .join("\n"); // Join each row with a new line.

        // Step 2: Create a Blob object from the CSV string
        // The Blob constructor takes an array of data and an object specifying the type (in this case, CSV).
        const blob = new Blob([decodeURIComponent(encodeURI(csvContent))], {
            type: "text/csv;charset=utf-8;",
        });

        // Step 3: Use file-saver's 'saveAs' function to trigger the download
        // The 'blob' contains the CSV data, and "vacation-likes-report.csv" is the default file name.
        saveAs(blob, "vacation-likes-report.csv");
    };



    const handleBackToVacations = () => {
        navigate("/vacations");
    };

    return (
        <div className="VacationReport">

            <button className="btn-back" onClick={handleBackToVacations}>
                ◀ Back to Vacations
            </button>


            <button className="btn-download" onClick={handleDownloadCSV}>
                Export CSV  ▼
            </button>

            <h2>Vacations Report</h2>
            <Bar data={chartData} options={chartOptions} />
        </div>
    );
}
