import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { VacationModel } from "../../../Models/VacationModel";
import { vacationActions } from "../../../Redux/store";
import { vacationsService } from "../../../Services/VacationsService";
import { VacationCards } from "../VacationCards/VacationCards";
import "./VacationList.css";

export function VacationList(): JSX.Element {
    const [vacations, setVacations] = useState<VacationModel[]>([]);
    const [currentPage, setCurrentPage] = useState<number>(1); // עמוד נוכחי
    const [itemsPerPage] = useState<number>(9); // מספר הכרטיסיות בעמוד
    const dispatch = useDispatch();

    useEffect(() => {
        vacationsService.getAllVacations()
            .then(vacations => {
                setVacations(vacations);
                const action = vacationActions.initVacations(vacations);
                dispatch(action);
            })
            .catch(err => alert(err.message));
    }, [dispatch]);

    // Calculate the index of the last and first item on the current page
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentVacations = vacations.slice(indexOfFirstItem, indexOfLastItem);

    // Forward to next page
    const nextPage = () => {
        if (currentPage < Math.ceil(vacations.length / itemsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };

    // Backward to previous page
    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    return (
        <div className="VacationList">
            <div className="vacation-cards-container">
                {currentVacations.map(vacation => <VacationCards key={vacation.id} vacation={vacation} />)}
            </div>

            {/* Buttons for Pagination */}
            <div className="pagination">
                <button onClick={prevPage} disabled={currentPage === 1}>Previous</button>
                <span>Page {currentPage}</span>
                <button onClick={nextPage} disabled={currentPage === Math.ceil(vacations.length / itemsPerPage)}>Next</button>
            </div>
        </div>
    );
}
